logstash_filter:
filter {

  if [type] == "apache" {

    dissect {
      mapping => {
        "message" => "%{clientip} %{ident} %{auth} [%{[@metadata][timestamp]}] \"%{verb} %{request} HTTP/%{httpversion}\" %{status} %{size}"
      }

      convert_datatype => {
        "size" => "int"
      }
    }

    date {
      match => ["[@metadata][timestamp]", "dd/MMM/yyyy:HH:mm:ss Z", "MMM dd HH:mm:ss"]
    }

  }

}
