logstash_filter:
filter {

  if [type] == "apache" {

    mutate {
      add_field => {
        "[ecs][version]" => "1.6.0"
        "[event][module]" => "apache"
        "[event][dataset]" => "access"
      }
    }

    dissect {
      mapping => {
        "message" => "%{[souce][ip]} - %{[user][name]} [%{[@metadata][timestamp]}] \"%{[http][method]} %{[http][url]} HTTP/%{[http][version]}\" %{[http][response][code]} %{[http][response][body_sent][bytes]}"
      }

      convert_datatype => {
        "[http][response][body_sent][bytes]" => "int"
        "[http][response][code]" => "int"
      }
    }

    date {
      match => ["[@metadata][timestamp]", "dd/MMM/yyyy:HH:mm:ss Z", "MMM dd HH:mm:ss"]
    }

  }

}
